import { useReducer, useState } from 'react';
import classes from './css/SeatsForm.module.css';
import Seat from './Seat';

const setSelectedSeats = (state, action) => {

}

const SeatsForm = props => {
    const firstRow = ['a1', 'a2', 'a3', 'a4', 'a5'];
    const secondRow = ['b1', 'b2', 'b3', 'b4', 'b5'];
    const thirdRow = ['c1', 'c2', 'c3', 'c4', 'c5'];
    const [selectedSeats, dispatchSeats] = useReducer([]);

    const selectHandler = input => {
        dispatchSeats(input);
    }
    return (
    <form>
    <tr>
        {firstRow.map(id => <td><Seat id={id} key={id} movieId={props.movieId} onChange={selectHandler}/></td>)}
    </tr>
    <tr>
        {secondRow.map(id => <td><Seat id={id} key={id} movieId={props.movieId} onChange={selectHandler}/></td>)}
    </tr>
    <tr>
        {thirdRow.map(id => <td><Seat id={id} key={id} movieId={props.movieId} onChange={selectHandler}/></td>)}
    </tr>
    </form>)
}

export default SeatsForm;
