import { useState } from 'react';
import classes from './css/Movie.module.css';

const Movie = props => {
    const movie = props.movie;
    const [isAvailable, setAvailable] = useState(true);

    if(isAvailable === true){
        const isAvailableP = <p style={{color: 'green'}}>Dispon√≠vel</p>
    }
    else {
        const isAvailableP = <p style={{color: 'red'}}>Esgotado</p>
    }


    return (
        <div className={classes.movie}>  
            <div className={classes['left-div']}>
            <img src={movie.image} alt='movie poster'/> 
            <div className={classes['movie-text']}>
                <h2>{movie.name} <small>{`(${movie.year})`}</small></h2>
                <p>{movie.time}</p>
            </div>
            <p></p>
            </div>
            <div className={classes['right-div']}>
                <p style={{color: 'green'}}>{isAvailableP}</p>
                <small>{movie.hour}</small>
            </div>
        </div>
    )
}

export default Movie;
