import { useReducer } from "react";
import MovieCtx from "./context";


const seatsChangeHandler = (state, seatsList) => {
    const taken_seats = [...state, ...seatsList];
    return (taken_seats);
}


const MovieCtxProvider = props => {

    const [m1TakenSeats, setm1TakenSeats] = useReducer(seatsChangeHandler, []);
    const [m2TakenSeats, setm2TakenSeats] = useReducer(seatsChangeHandler, []);
    const [m3TakenSeats, setm3TakenSeats] = useReducer(seatsChangeHandler, []);
    const [m4TakenSeats, setm4TakenSeats] = useReducer(seatsChangeHandler, []);

    const movieCtx = [
        {
            id: 'm1', is_soldoff: false, onSeatsBought: (seats) => {setm1TakenSeats(seats)}, seats_taken: m1TakenSeats
        }, 
        {
            id: 'm2', is_soldoff: false, onSeatsBought: (seats) => {setm2TakenSeats(seats)}, seats_taken: m2TakenSeats
        },
        {
            id: 'm3', is_soldoff: false, onSeatsBought: (seats) => {setm3TakenSeats(seats)}, seats_taken: m3TakenSeats
        },
        {
            id: 'm4', is_soldoff: false, onSeatsBought: (seats) => {setm4TakenSeats(seats)}, seats_taken: m4TakenSeats
        },
    ]

    return (
        <MovieCtx.Provider>
            {props.children}
        </MovieCtx.Provider>
    )
}

export default MovieCtxProvider;
