import { useContext, useRef } from 'react';
import MovieCtx from '../../store/context';
import classes from './css/Seat.module.css';

const Seat = props => {
    const inputRef = useRef();
    const seatCtx = useContext(MovieCtx);
    const movieIndex = seatCtx.findIndex(movie => movie.id === props.movieId);
    
    let isTaken = false;

    if (seatCtx[movieIndex].seats_taken.includes(props.id)){
        isTaken = true;
    }   

    const checkHandler = props => {
        if (inputRef.current.checked) {
            props.onChange({id: props.id, checked: true});
        }
        else{
            props.onChange({id: props.id, checked: false});
        }
    }


    return (
        <>
            <input type='checkbox' id={props.id} ref={inputRef} disabled={isTaken} onChange={checkHandler}/>
        </>
    )
}

export default Seat;
