import {  useEffect, useReducer, useRef } from 'react';
import classes from './css/TicketType.module.css';


const Input = props => {
    const inputRef = useRef();

    const subtractHandler = () => {

    }

    const addtHandler = () => {

    }

    return (
        <div>
            <button onClick={subtractHandler}>-</button>
            <input ref={inputRef} value={props.value} type='number' max={props.amount} min='0'/>
            <button onClick={addtHandler}>+</button>
        </div>
    )
}

const setTickets = (state, action) => {
    switch (action.change) {
        case 'ADDED_SEAT': {
            return {normal: state.normal + 1, half: state.half, total: action.seatsAmount}
        }
        case 'REMOVED_SEAT': {
            if (state.half > state.normal) {
                return {normal: state.normal, half: state.half - 1, total: action.seatsAmount}
            }
            else {
                return {normal: state.normal - 1, half: state.half, total: action.seatsAmount}
            }
        }
        default: return state;
    }
    }


const TicketType = props => {
    const selectedSeats = props.selectedSeats;

    const [ticketTypes, dispatchTicket] = useReducer(setTickets, {normal: 0, half: 0, total: 0})
    console.log(ticketTypes);

    useEffect(() => {
        seatsAmount = selectedSeats.length;
        if (seatsAmount > ticketTypes.total) {
            console.log (ticketTypes.total)
            console.log()
            dispatchTicket({change: 'ADDED_SEAT', seatsAmount: seatsAmount});
        }
        else {
            dispatchTicket({change: 'REMOVED_SEAT', seatsAmount: seatsAmount});
        }
    }, [seatsAmount])

    return(
        <>
        <p>Assentos selecionados: {selectedSeats.map((seat, i) => i === selectedSeats.length - 1?
         `${seat}`: `${seat}, `)}</p>

            <Input/>
            <Input/>

        <button type='submit'>aaa</button>
        </>
    )
}

export default TicketType;
